void Home_ReflowViewBase::buttonCallbackHandler(const touchgfx::AbstractButton& src)
{
    if (&src == &btnReflowStart)
    {
        //StartReflow
        //When btnReflowStart clicked call virtual function
        //Call StartReflow
        StartReflow();
    }
    if (&src == &btnStopStop)
    {
        //StopReflow
        //When btnStopStop clicked call virtual function
        //Call StopReflow
        StopReflow();
    }
    if (&src == &btnReflowMenu)
    {
        //ReflowToMenu
        //When btnReflowMenu clicked change screen to Menu
        //Go to Menu with no screen transition
        application().gotoMenuScreenNoTransition();
    }
}
