void SettingsViewBase::buttonCallbackHandler(const touchgfx::AbstractButton& src)
{
    if (&src == &btnBack)
    {
        //SettingsToMenu
        //When btnBack clicked change screen to Menu
        //Go to Menu with no screen transition
        application().gotoMenuScreenNoTransition();
    }
    if (&src == &btnSetPidP)
    {
        //SettingsToPid_P
        //When btnSetPidP clicked change screen to Setup_Pid
        //Go to Setup_Pid with no screen transition
        application().gotoSetup_PidScreenNoTransition();
    }
    if (&src == &btnSetPidI)
    {
        //SettingsToPid_I
        //When btnSetPidI clicked change screen to Setup_Pid
        //Go to Setup_Pid with no screen transition
        application().gotoSetup_PidScreenNoTransition();
    }
    if (&src == &btnSetPidD)
    {
        //SettingsToPid_D
        //When btnSetPidD clicked change screen to Setup_Pid
        //Go to Setup_Pid with no screen transition
        application().gotoSetup_PidScreenNoTransition();
    }
    if (&src == &toggleBtnProbes)
    {
        //ProbesNoSelect
        //When toggleBtnProbes clicked call virtual function
        //Call ProbesNoSelect
        ProbesNoSelect();
    }
    if (&src == &toggleBtnSSR)
    {
        //SSRNoSelect
        //When toggleBtnSSR clicked call virtual function
        //Call SSRNoSelect
        SSRNoSelect();
    }
    if (&src == &toggleBtnCF)
    {
        //CFSelect
        //When toggleBtnCF clicked call virtual function
        //Call CFSelect
        CFSelect();
    }
    if (&src == &toggleBtnAUX1)
    {
        //outputAUX1
        //When toggleBtnAUX1 clicked call virtual function
        //Call outputAUX1
        outputAUX1();
    }
    if (&src == &toggleBtnAUX2)
    {
        //outputAUX2
        //When toggleBtnAUX2 clicked call virtual function
        //Call outputAUX2
        outputAUX2();
    }
}
